/// ```
/// use regex::RegexBuilder;
///
/// let pat = r"
///     \b
///     (?<first>\p{Uppercase}\w*)  # always start with uppercase letter
///     [\s--\n]+                   # whitespace should separate names
///     (?: # middle name can be an initial!
///         (?:(?<initial>\p{Uppercase})\.|(?<middle>\p{Uppercase}\w*))
///         [\s--\n]+
///     )?
///     (?<last>\p{Uppercase}\w*)
///     \b
