/// ```
/// use regex_automata::{
///     dfa::{dense, Automaton, StartKind},
///     nfa::thompson::NFA,
///     Anchored, HalfMatch, Input, MatchKind,
/// };
///
/// let haystack = "123foobar456".as_bytes();
/// let pattern = r"[a-z]+r";
