/// Unconditionally add a new empty DFA state. If adding it would exceed
/// any limits (configured or hard-coded), then an error is returned. The
/// ID of the new state is returned on success.
///
/// The added state is *not* a match state.
862 fn add_empty_state(&mut self) -> Result<StateID, BuildError> {
863     let state_limit = Transition::STATE_ID_LIMIT;
864     // Note that unlike dense and lazy DFAs, we specifically do NOT
865     // premultiply our state IDs here. The reason is that we want to pack
866     // our state IDs into 64-bit transitions with other info, so the fewer
867     // the bits we use for state IDs the better. If we premultiply, then
868     // our state ID space shrinks. We justify this by the assumption that
869     // a one-pass DFA is just already doing a fair bit more work than a
870     // normal DFA anyway, so an extra multiplication to compute a state
871     // transition doesn't seem like a huge deal.
872     let next_id = self.dfa.table.len() >> self.dfa.stride2();
873     let id = StateID::new(next_id)
874         .map_err(|_| BuildError::too_many_states(state_limit))?;
875     if id.as_u64() > Transition::STATE_ID_LIMIT {
876         return Err(BuildError::too_many_states(state_limit));
877     }
878     self.dfa
879         .table
880         .extend(core::iter::repeat(Transition(0)).take(self.dfa.stride()));
881     // The default empty value for 'PatternEpsilons' is sadly not all
882     // zeroes. Instead, a special sentinel is used to indicate that there
883     // is no pattern. So we need to explicitly set the pattern epsilons to
884     // the correct "empty" PatternEpsilons.
885     self.dfa.set_pattern_epsilons(id, PatternEpsilons::empty());
886     if let Some(size_limit) = self.config.get_size_limit() {
887         if self.dfa.memory_usage() > size_limit {
888             return Err(BuildError::exceeded_size_limit(size_limit));
889         }
890     }
891     Ok(id)
892 }

