    fn poll(&self) -> Option<&T> {
        if self.state.load(Ordering::Acquire) == LAZY_STATE_DONE {
            Some(unsafe { &*(*self.data.as_ptr()).as_ptr() })
        } else {
            None
        }
    }
}

