 51 fn internal_encode(&self, input: &[u8], output: &mut [u8]) -> usize {
 52     let mut input_index: usize = 0;
 53 
 54     const BLOCKS_PER_FAST_LOOP: usize = 4;
 55     const LOW_SIX_BITS: u64 = 0x3F;
 56 
 57     // we read 8 bytes at a time (u64) but only actually consume 6 of those bytes. Thus, we need
 58     // 2 trailing bytes to be available to read..
 59     let last_fast_index = input.len().saturating_sub(BLOCKS_PER_FAST_LOOP * 6 + 2);
 60     let mut output_index = 0;
 61 
 62     if last_fast_index > 0 {
 63         while input_index <= last_fast_index {
 64             // Major performance wins from letting the optimizer do the bounds check once, mostly
 65             // on the output side
 66             let input_chunk =
 67                 &input[input_index..(input_index + (BLOCKS_PER_FAST_LOOP * 6 + 2))];
 68             let output_chunk =
 69                 &mut output[output_index..(output_index + BLOCKS_PER_FAST_LOOP * 8)];
 70 
 71             // Hand-unrolling for 32 vs 16 or 8 bytes produces yields performance about equivalent
 72             // to unsafe pointer code on a Xeon E5-1650v3. 64 byte unrolling was slightly better for
 73             // large inputs but significantly worse for 50-byte input, unsurprisingly. I suspect
 74             // that it's a not uncommon use case to encode smallish chunks of data (e.g. a 64-byte
 75             // SHA-512 digest), so it would be nice if that fit in the unrolled loop at least once.
 76             // Plus, single-digit percentage performance differences might well be quite different
 77             // on different hardware.
 78 
 79             let input_u64 = read_u64(&input_chunk[0..]);
 80 
 81             output_chunk[0] = self.encode_table[((input_u64 >> 58) & LOW_SIX_BITS) as usize];
 82             output_chunk[1] = self.encode_table[((input_u64 >> 52) & LOW_SIX_BITS) as usize];
 83             output_chunk[2] = self.encode_table[((input_u64 >> 46) & LOW_SIX_BITS) as usize];
 84             output_chunk[3] = self.encode_table[((input_u64 >> 40) & LOW_SIX_BITS) as usize];
 85             output_chunk[4] = self.encode_table[((input_u64 >> 34) & LOW_SIX_BITS) as usize];
 86             output_chunk[5] = self.encode_table[((input_u64 >> 28) & LOW_SIX_BITS) as usize];
 87             output_chunk[6] = self.encode_table[((input_u64 >> 22) & LOW_SIX_BITS) as usize];
 88             output_chunk[7] = self.encode_table[((input_u64 >> 16) & LOW_SIX_BITS) as usize];
 89 
 90             let input_u64 = read_u64(&input_chunk[6..]);
 91 
 92             output_chunk[8] = self.encode_table[((input_u64 >> 58) & LOW_SIX_BITS) as usize];
 93             output_chunk[9] = self.encode_table[((input_u64 >> 52) & LOW_SIX_BITS) as usize];
 94             output_chunk[10] = self.encode_table[((input_u64 >> 46) & LOW_SIX_BITS) as usize];
 95             output_chunk[11] = self.encode_table[((input_u64 >> 40) & LOW_SIX_BITS) as usize];
 96             output_chunk[12] = self.encode_table[((input_u64 >> 34) & LOW_SIX_BITS) as usize];
 97             output_chunk[13] = self.encode_table[((input_u64 >> 28) & LOW_SIX_BITS) as usize];
 98             output_chunk[14] = self.encode_table[((input_u64 >> 22) & LOW_SIX_BITS) as usize];
 99             output_chunk[15] = self.encode_table[((input_u64 >> 16) & LOW_SIX_BITS) as usize];
100 
101             let input_u64 = read_u64(&input_chunk[12..]);
102 
103             output_chunk[16] = self.encode_table[((input_u64 >> 58) & LOW_SIX_BITS) as usize];
104             output_chunk[17] = self.encode_table[((input_u64 >> 52) & LOW_SIX_BITS) as usize];
105             output_chunk[18] = self.encode_table[((input_u64 >> 46) & LOW_SIX_BITS) as usize];
106             output_chunk[19] = self.encode_table[((input_u64 >> 40) & LOW_SIX_BITS) as usize];
107             output_chunk[20] = self.encode_table[((input_u64 >> 34) & LOW_SIX_BITS) as usize];
108             output_chunk[21] = self.encode_table[((input_u64 >> 28) & LOW_SIX_BITS) as usize];
109             output_chunk[22] = self.encode_table[((input_u64 >> 22) & LOW_SIX_BITS) as usize];
110             output_chunk[23] = self.encode_table[((input_u64 >> 16) & LOW_SIX_BITS) as usize];
111 
112             let input_u64 = read_u64(&input_chunk[18..]);
113 
114             output_chunk[24] = self.encode_table[((input_u64 >> 58) & LOW_SIX_BITS) as usize];
115             output_chunk[25] = self.encode_table[((input_u64 >> 52) & LOW_SIX_BITS) as usize];
116             output_chunk[26] = self.encode_table[((input_u64 >> 46) & LOW_SIX_BITS) as usize];
117             output_chunk[27] = self.encode_table[((input_u64 >> 40) & LOW_SIX_BITS) as usize];
118             output_chunk[28] = self.encode_table[((input_u64 >> 34) & LOW_SIX_BITS) as usize];
119             output_chunk[29] = self.encode_table[((input_u64 >> 28) & LOW_SIX_BITS) as usize];
120             output_chunk[30] = self.encode_table[((input_u64 >> 22) & LOW_SIX_BITS) as usize];
121             output_chunk[31] = self.encode_table[((input_u64 >> 16) & LOW_SIX_BITS) as usize];
122 
123             output_index += BLOCKS_PER_FAST_LOOP * 8;
124             input_index += BLOCKS_PER_FAST_LOOP * 6;
125         }
126     }
127 
128     // Encode what's left after the fast loop.
129 
130     const LOW_SIX_BITS_U8: u8 = 0x3F;
131 
132     let rem = input.len() % 3;
133     let start_of_rem = input.len() - rem;
134 
135     // start at the first index not handled by fast loop, which may be 0.
136 
137     while input_index < start_of_rem {
138         let input_chunk = &input[input_index..(input_index + 3)];
139         let output_chunk = &mut output[output_index..(output_index + 4)];
140 
141         output_chunk[0] = self.encode_table[(input_chunk[0] >> 2) as usize];
142         output_chunk[1] = self.encode_table
143             [((input_chunk[0] << 4 | input_chunk[1] >> 4) & LOW_SIX_BITS_U8) as usize];
144         output_chunk[2] = self.encode_table
145             [((input_chunk[1] << 2 | input_chunk[2] >> 6) & LOW_SIX_BITS_U8) as usize];
146         output_chunk[3] = self.encode_table[(input_chunk[2] & LOW_SIX_BITS_U8) as usize];
147 
148         input_index += 3;
149         output_index += 4;
150     }
151 
152     if rem == 2 {
153         output[output_index] = self.encode_table[(input[start_of_rem] >> 2) as usize];
154         output[output_index + 1] =
155             self.encode_table[((input[start_of_rem] << 4 | input[start_of_rem + 1] >> 4)
156                 & LOW_SIX_BITS_U8) as usize];
157         output[output_index + 2] =
158             self.encode_table[((input[start_of_rem + 1] << 2) & LOW_SIX_BITS_U8) as usize];
159         output_index += 3;
160     } else if rem == 1 {
161         output[output_index] = self.encode_table[(input[start_of_rem] >> 2) as usize];
162         output[output_index + 1] =
163             self.encode_table[((input[start_of_rem] << 4) & LOW_SIX_BITS_U8) as usize];
164         output_index += 2;
165     }
166 
167     output_index
168 }
